ScrollTrigger.create({
    trigger: "#posDetails",
    start: "10% 25%",
    endTrigger: "#project1",
    end: "85% 40%+=100px",
    onEnter: (self) => {
        posDetails.classList.toggle('sm:w-[45vw]')
        posDetails.classList.toggle('2xl:w-[38vw]')
        posDetails.classList.toggle('sm:w-[50vw]')
    },
    onEnterBack: (self) => {
        posDetails.classList.toggle('sm:w-[45vw]')
        posDetails.classList.toggle('2xl:w-[38vw]')
        posDetails.classList.toggle('sm:w-[50vw]')
    },
    onLeave: (self) => {
        posDetails.classList.toggle('sm:w-[45vw]')
        posDetails.classList.toggle('2xl:w-[38vw]')
        posDetails.classList.toggle('sm:w-[50vw]')
    },
    onLeaveBack: (self) => {
        posDetails.classList.toggle('sm:w-[45vw]')
        posDetails.classList.toggle('2xl:w-[38vw]')
        posDetails.classList.toggle('sm:w-[50vw]')
    },
});

